import React, { Component } from 'react';
import axios from 'axios';


class FullPlan extends Component {
    state = {
        loadedPlan: null
    }

    componentDidUpdate () {
        if ( this.props.id ) {
            if ( !this.state.loadedPlan || (this.state.loadedPlan && this.state.loadedPlan.id !== this.props.id) ) {
                axios.get( 'https://academicbusinessplanner.firebaseio.com/businessplans/' + this.props.id + 'json' )
                    .then( response => {
                        // console.log(response);
                        this.setState( { loadedPlan: response.data } );
                    } );
            }
        }
    }

    render () {
        let post = <p>Please select a Post!</p>;
        if ( this.props.id ) {
            post = <p>Loading...!</p>;
        }
        if ( this.state.loadedPlan ) {
            post = (
                <div className="FullPost">
                    <h1>{this.state.loadedPlan.data.external[0].legal}</h1>
                    <p>{this.state.loadedPlan.data.external[0].political}</p>

                </div>

            );
        }
        return post;
    }
}

export default FullPlan;
